import { React, useState } from 'react';
// import Header from "../components/NavBar.js" 
import {
    Box,
    FormControl,
    FormLabel,
    FormErrorMessage,
    FormHelperText,
    Input,
    Button,
    Flex,
    Heading,
    InputLeftElement,
    InputGroup,
    Spacer
  } from '@chakra-ui/react'

import { AtSignIcon, LockIcon } from '@chakra-ui/icons'


// import axios from 'axios'
// const url = "http://localhost:3030/user/signup/" 

function Register() {
    const [nameInput, setNameInput] = useState('')
    const [passInput, setPassInput] = useState('')

    const handleNameInputChange = (e) => setNameInput(e.target.value)
    const handlePassInputChange = (e) => setPassInput(e.target.value)

    const isNameError = nameInput === ''
    const isPassError = passInput === ''

    return (
        <Box 
        h="100%"
        position="fixed"
        w="100%"
        background="linear-gradient(134.89deg, #D86FCC 0.46%, #01055B 100.26%)"
        color="white"
        >
            {/* <Header /> */}
            <Flex
                align="center"
                direction="column"
                justify='space-evenly'
                mr="30%"
                ml="30%"
                mt="100px"
                gap="10px"
            >
                <Heading as='h1' size='xl'>Sign Up</Heading>

                <Heading textAlign="center" as='h2' size='sm'>Sign up and start managing your group expenditures!</Heading>
                <Spacer />
                <Spacer />
                <Spacer />
                <Box w="50%">
                    <FormControl isInvalid={isNameError}>
                        <FormLabel htmlFor='username'></FormLabel>
                        <InputGroup>
                            <InputLeftElement
                            pointerEvents='none'
                            children={<AtSignIcon color='gray.300' />}
                            />
                            <Input
                                id='username'
                                type='username'
                                value={nameInput}
                                onChange={handleNameInputChange}
                                placeholder='Username'
                                // bg="rgba(255, 255, 255, 0.1)"
                                variant='filled'
                            />
                        </InputGroup>

                        {!isNameError ? (
                            // <FormHelperText color="white">
                            // Enter a unique username.
                            // </FormHelperText>
                            console.log("hey")
                        ) : (
                            <FormErrorMessage>A username is required.</FormErrorMessage>
                        )}
                    </FormControl>
                    <FormControl isInvalid={isPassError}>
                        <FormLabel htmlFor='password'></FormLabel>
                        <InputGroup>
                            <InputLeftElement 
                            pointerEvents='none'
                            children={<LockIcon color='gray.300' />}
                            />
                            <Input
                                id='password'
                                type='password'
                                value={passInput}
                                onChange={handlePassInputChange}
                                placeholder='Password'
                                variant='filled'
                            />
                        </InputGroup>

                        {!isPassError ? (
                            <FormHelperText color="white">
                            Enter a password you won't forget!
                            </FormHelperText>
                        ) : (
                            <FormErrorMessage>A password is required.</FormErrorMessage>
                        )}
                    </FormControl>
                    <Button type="submit" bg='#CA41D6' size='lg' _hover={{ bg: '#e261ed' }} width="full" mt={4}>
                        SIGN UP
                    </Button>

                </Box>

            </Flex>
        </Box>

    );
    // return(
    //     <Box>
    //         <FormControl isRequired>
    //             <FormLabel htmlFor='username'>Username</FormLabel>
    //             <Input id='username' placeholder='Username' />
    //         </FormControl>
    //         <FormControl>
    //             <FormLabel htmlFor='password'>Password</FormLabel>
    //             <Input id='email' type='email' />
    //             <FormHelperText>We'll never share your email.</FormHelperText>
    //         </FormControl>

    //     </Box>

    // );
}

export default Register;
